/******************************************************************************
 * FLY-IN.JS - Javascript that will animate any element to fly onto page
 * Author: McKay Smalley - May 2014
 * REQUIRES:
 *         - jQuery
 *         - A main tag to be wrapped inside body
 * ***************************************************************************/
function checkFlightStatus(){var e=document.getElementsByClassName("fly-in-right");var t=document.getElementsByClassName("fly-in-left");for(var n=0;n<e.length;n++){if(isVisible(e[n])&&e[n].style.visibility=="hidden"){e[n].style.visibility="visible";flyIn(e[n],flyDistance)}}for(n=0;n<t.length;n++){if(isVisible(t[n])&&t[n].style.visibility=="hidden"){t[n].style.visibility="visible";flyIn(t[n],-flyDistance)}}if(isVisible(bottomFlyer())){flewIn=true;for(n=0;n<e.length;n++){if(e[n].style.visibility!="visible"){e[n].style.visibility="visible";e[n].style.right="0px";e[n].style.opacity=1}}for(n=0;n<t.length;n++){if(t[n].style.visibility!="visible"){t[n].style.visibility="visible";t[n].style.left="0px";t[n].style.opacity=1}}}}function isVisible(e){if(e===null){return true}var t=$(window).scrollTop();var n=t+$(window).height();var r=$(e).offset().top;var i=r+e.offsetHeight;if(t<r&&r<n&&t<i&&i<n)return true;else return false}function bottomFlyer(){var e=Array.prototype.slice.call(document.getElementsByClassName("fly-in-right")).concat(Array.prototype.slice.call(document.getElementsByClassName("fly-in-left")));var t=e[0];for(var n=1;n<e.length;n++){if(e[n].offsetHeight>0&&$(e[n]).offset().top+e[n].offsetHeight>$(t).offset().top+t.offsetHeight){t=e[n]}}if(t.offsetHeight<=0)return null;else return t}function flyIn(e,t){e.style.transition="all 0.5s ease-out";e.style.WebkitTransition="all 0.5s ease-out";e.style.OTransition="all 0.5s ease-out";e.style.msTransition="all 0.5s ease-out";e.style.MozTransition="all 0.5s ease-out";e.style.transform="translateX("+t+"px)";e.style.WebkitTransform="translateX("+t+"px)";e.style.OTransform="translateX("+t+"px)";e.style.msTransform="translateX("+t+"px)";e.style.MozTransform="translateX("+t+"px)";e.style.opacity=1}var flewIn;var flyDistance;$(window).load(function(){if(!flewIn)checkFlightStatus()});$(document).scroll(function(){if(!flewIn)checkFlightStatus()});$(window).resize(function(){if(!flewIn)checkFlightStatus()});$(document).ready(function(){if("undefined"==typeof jQuery)throw new Error("Fly-in JavaScript requires jQuery");else{flewIn=false;flyDistance=-$("main").width();var e=document.getElementsByClassName("fly-in-right");var t=document.getElementsByClassName("fly-in-left");for(var n=0;n<e.length;n++){e[n].style.visibility="hidden";e[n].style.position="relative";e[n].style.right=flyDistance+"px";e[n].style.opacity=0}for(n=0;n<t.length;n++){t[n].style.visibility="hidden";t[n].style.position="relative";t[n].style.left=flyDistance+"px";t[n].style.opacity=0}$("main").css("overflow","hidden")}})